css`
    .elevation-0 {
        transition: var(--md-sys-elevation-transition);
        box-shadow: var(--md-sys-elevation-level-0);
    }
`

css`
    .elevation-1 {
        position: relative;
        overflow: hidden;
        transition: var(--md-sys-elevation-transition);
        box-shadow: var(--md-sys-elevation-level-1);
    }

    .elevation-1:before {
        content: '';
        position: absolute;
        pointer-events: none;
        left: 0;
        top: 0;
        width: 100%;
        height: 100%;
        opacity: 0.05;
        background-color: var(--md-sys-color-primary);
    }
`

